import React, { Component } from 'react'

class Quotes extends Component {
	constructor(props) {
		super(props);
		this.state = {
			quote: "The only person you are destined to become is the person you decide to be.",
			author: "- Ralph Waldo Emerson"
		}
		this.handleSubmit = this.handleSubmit.bind(this);
	}
	handleSubmit(e) {
		e.preventDefault();
		fetch("http://quotes.rest/quote/random")
		.then(res => res.json())
		.then(quote => this.setState({
			quote: quote.body,
			author: quote.author
		}))
	}

	render() {
		return (
			<div id= "quote-box">
				<div id= "text">
					<h3>{this.state.quote}</h3>
				</div>
				<div id= "author">
					<p>{this.state.author}</p>
				</div>
				<div id= "new-quote">
					<button type= "submit" onSubmit={this.handleSubmit} className= "btn btn-primary">New Quote</button>
				</div>
				<div id= "tweet-quote">
					<a href= "twitter.com/intent/tweet">
						Tweet Quote
					</a>
				</div>
			</div>
		)
	}
}
export default Quotes;
