import React from "react";
import Home from "./Pages/Home";
import { Route, Switch } from 'react-router-dom';
import About from "./Pages/About";
import Belief from "./Pages/Belief";
import Gallery from "./Pages/Gallery";
import Team from "./Pages/Team";
import Give from "./Pages/Give";
import Stories from "./Pages/Stories";
import Testimonies from "./Pages/Testimonies";
import Confession from "./Pages/Confession";
import Events from "./Pages/Events";
import Sermon from "./Pages/Sermon";
import Error from "./Error";
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
    }
      toggleClass = () => {
       this.setState((prevState) => ({
              active: !prevState.active
            }));
        const element = document.body;
        const image = document.querySelector(".counts .image img");
        this.state.active ? element.classList.add("darkTheme") : element.classList.remove("darkTheme");
        if(image){
            this.state.active ? image.classList.add("darkTheme") : image.classList.remove("darkTheme")
        };
        }
    render(){

    return (
      <main>
          <Switch>
              <Route path="/" render={renderProps => (
                  <Home prop = {this.toggleClass} {...renderProps}/>
              )} exact/>
              <Route path="/about" render={renderProps => (
                  <About prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/belief" render={renderProps => (
                  <Belief prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/gallery" render={renderProps => (
                  <Gallery prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/team"render={renderProps => (
                  <Team prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/give" render={renderProps => (
                  <Give prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/stories" render={renderProps => (
                  <Stories prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/testimonies" render={renderProps => (
                  <Testimonies prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/confessions" render={renderProps => (
                  <Confession prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/events" render={renderProps => (
                  <Event prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route path="/sermon" render={renderProps => (
                  <Sermon prop = {this.toggleClass} {...renderProps}/>
              )}/>
              <Route component={Error} />
          </Switch>
      </main>
    )
    }
}
