import React from "react";
import Home from "./Pages/Home";
import { Route, Switch } from 'react-router-dom';
import About from "./Pages/About";
import Belief from "./Pages/Belief";
import Gallery from "./Pages/Gallery";
import Team from "./Pages/Team";
import Give from "./Pages/Give";
import Stories from "./Pages/Stories";
import Testimonies from "./Pages/Testimonies";
import Confession from "./Pages/Confession";
import Events from "./Pages/Events";
import Sermon from "./Pages/Sermon";
import Error from "./Error";
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
    }
      toggleClass = () => {
       this.setState((prevState) => ({
              active: !prevState.active
            }));
        }
    render(){
        const element = document.body;
        const image = document.querySelector(".counts .image img");
        this.state.active ? element.classList.add("darkTheme") : element.classList.remove("darkTheme");
        if(image){
            this.state.active ? image.classList.add("darkTheme") : image.classList.remove("darkTheme")
        };
    return (
      <main>
          <Switch>
              <Route props={"toggleClass"} path="/" component={Home} exact/>
              <Route path="/about" component={About} onclick={this.toggleClass}/>
              <Route path="/belief" component={Belief} onclick={this.toggleClass}/>
              <Route path="/gallery" component={Gallery} onclick={this.toggleClass}/>
              <Route path="/team" component={Team} onclick={this.toggleClass}/>
              <Route path="/give" component={Give} onclick={this.toggleClass}/>
              <Route path="/stories" component={Stories} onclick={this.toggleClass}/>
              <Route path="/testimonies" component={Testimonies} onclick={this.toggleClass}/>
              <Route path="/confessions" component={Confession} onclick={this.toggleClass}/>
              <Route path="/events" component={Events} onclick={this.toggleClass}/>
              <Route path="/sermon" component={Sermon} onclick={this.toggleClass}/>
              <Route component={Error} />
          </Switch>
      </main>
    )
    }
}
