import './App.css';
import React, { Component } from 'react'
class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			text: "I attribute my success to this: I never gave or took any excuse",
			author: "Tony Robbins" ,
			bkgColor: 555
		}
		this.handleClick = this.handleClick.bind(this);
	}

		handleClick() {
		fetch("https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/")
		.then(res => res.json())
		.then(data => {
			this.setState({
				text: data.quotes[Math.floor(Math.random() *102)].quote,
				author: data.quotes[Math.floor(Math.random() *102)].author,
				bkgColor:  Math.floor(Math.random()*1000)
		})})
		.catch(error => console.error("Error: ", error))

}

	render() {
		console.log(this.state.author);
		return (
      <div class= "appDiv" style={{backgroundColor: `#${this.state.bkgColor}`}}>
			<div id= "quote-box">
				<div id= "text">
				<i class="fa fa-quote-left" aria-hidden="true"></i>
				<h3>{this.state.text}</h3>
				<i class="fa fa-quote-right" aria-hidden="true"></i>
				</div>
				<div id= "author">
					<p>-{this.state.author}</p>
				</div>
				<div id= "new-quote">
					<button onClick={this.handleClick} className= "btn btn-primary">New Quote</button>
				</div>
				<div >
					<a id= "tweet-quote" href= "twitter.com/intent/tweet" className= "btn btn-primary">
					<i class="fa fa-twitter" aria-hidden="true"></i> Tweet!
					</a>
				</div>
			</div>
      </div>
		) 
	}
}
export default App;
