import React, { useEffect } from 'react';
import Header from "../Components/Header";
import Footer from "../Components/Footer";

const Contact = () => {
    useEffect(() => {
        const navList = document.querySelectorAll("li");
        navList.forEach(list => {
            if (list.classList.contains("active")) {
                list.classList.remove("active");
            }
        })
        navList[2].style.display = "none";
        navList[4].classList.add("active");
    })
    return (
        <>
        <div className="container-xl container-fluid">
        <Header/>
           <div className="contact">
               <h2 className="text-center"><strong>Get In Touch</strong></h2>
               <div className="contact-form">
                   <div className="name-email">
                    <input type="name" name="name" required minLength="3" placeholder="Name"/>
                    <input type="email" name="email" required minLength="10" placeholder="Email"/>
                   </div>
                   <div className="subject-message">
                       <input type="name" name="message" required minLength="5" placeholder="Subject"/>
                       <textarea placeholder="Your message"></textarea>
                   </div>
                   <div className="submit-btn">
                       <button type="submit">Send Message</button>
                   </div>
               </div>
           </div>
        <Footer />
        </div>
        </>
    )
}
export default Contact;
