import React, { Component } from 'react'
class Quotes extends Component {
	constructor(props) {
		super(props);
		this.state = {
			quotes: []
		}
		this.handleClick = this.handleClick.bind(this);
	}

		handleClick() {
		fetch("https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/")
		.then(res => res.json())
		.then(data => {
			this.setState({
				quotes: data.quotes
		})})
		.catch(error => console.error("Error: ", error))
}

	render() {
		const randomNumber = Math.floor(Math.random() * this.state.quotes.length);


		return (
			<div id= "quote-box">
				<div id= "text">
				<h3>{this.state.quotes[randomNumber]}</h3>
				</div>
				<div id= "author">
					<p>-{this.state.quotes[randomNumber]}</p>
				</div>
				<div id= "new-quote">
					<button onClick={this.handleClick} className= "btn btn-primary">New Quote</button>
				</div>
				<div >
					<a id= "tweet-quote" href= "twitter.com/intent/tweet" className= "btn btn-primary">
						Tweet Quote
					</a>
				</div>
			</div>
		)
	}
}
export default Quotes;
