                failed.style.display = "none";
                success.innerHTML = data.error ? data.error + " Please try again" : data.message;
                }
			saved().catch(err => {
                failed.style.display = "flex";
                loading.style.display = "none";
                failed.innerHTML = err;
            });
        };
        budgetForm.onsubmit = saveNewBudget;

