import React from "react";
import Navbar from "../Components/Navbar";

const Signup = () => {
    useEffect(() => {
        const loginForm = document.getElementById("login-form");

		const login = (e) => {
			e.preventDefault();
			const formData = new FormData(loginForm);
			const username = formData.get("username");
			const password = formData.get("password");

			const Params = {
				headers: {
					"Content-type": "application/JSON",
				},
				body: JSON.stringify({
					username: username,
					password: password,
				}),
				method: "POST",
			};

			async function auth() {
				const res = await fetch(
					"https://budgetify20.herokuapp.com/api/login",
					Params
				);
			//	const data = await res.json();
				console.log(res);
			}
			auth();
		};
        loginForm.onsubmit = login;

    })



    return (
        <div>
            <Navbar />
        <section id="signup">
    <img src="register-user.png" alt="" className="user-icon-signup"/>
    <h3>Register</h3>
    <form id="signup-form">
        <div className="user-details-signup">
            <div className="input-field-signup">
                <label for="username" className="user-1">USERNAME</label>
                <input type="text" name="username" id="username-signup" placeholder="Enter Username"required />
            </div>
            <div className="input-field-signup">
                <label>FULLNAME</label>
                <input type="text" name="fullname" id="fullname-signup" placeholder="Enter Fullname" required />
            </div>
        </div>
        <div className="user-details-signup">
            <div className="input-field-signup">
                <label for="e-mail" className="user-1-signup">E-MAIL</label>
                <input type="email" name="email" id="email-signup" placeholder="Enter E-mail" required />
            </div>
            <div className="input-field-signup">
                <label for="">PASSWORD</label>
            <input type="password" name="password" id="password-signup" placeholder="Input Password" required />
            </div>
        </div>
            <input type="submit" value="Sign Up" id="Sign-up" />
        <div className="Successful-login">Registeration Successful!</div>
        <div className="Unsuccessful-login">Use a valid user input</div>
    </form>
</section>
<div className="illustration-signup"></div>
</div>
    )
}

export default Signup;
