import React from 'react';
import Navbar from "./Navbar";
import { Link } from "react-router-dom";

export default class Header extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
        this.toggleClass= this.toggleClass.bind(this);
    }
      toggleClass() {
        const control = document.querySelector(".custom-control-input");
          const element = document.body;
          const image = document.querySelector(".counts .image img");
          control.checked ? element.classList.add("darkTheme") : element.classList.remove("darkTheme");
          control.checked ? image.classList.add("darkTheme") : image.classList.remove("darkTheme");
          this.setState((prevState) => ({
              active: !prevState.active
            }));
        }
    render(){
    return (
    <>
    <header id="header" className="fixed-top d-flex align-items-center">
        <div className="custom-control custom-switch">
			<input type="checkbox"
			className="custom-control-input"
			id="customSwitches"
			onClick={this.toggleClass}/>
  			<label className="custom-control-label" for="customSwitches">Day/Night Mode</label>
		</div>
        <div className="container d-flex align-items-center">
            <div className="logo mr-auto">
                <h1 className="text-light"><Link to="/">
                <img src="assets/img/scm-header.png" alt="" className="img-fluid"/>
                <span>  SCM UI</span></Link></h1>
            </div>
            <nav className="nav-menu d-none d-lg-block">
            <ul>
                <li className="active"><Link to="/">Home</Link></li>
                <li className="drop-down"><a href="/about">Explore!</a>
                  <ul>
                    <li><Link to="/stories">SCM Stories</Link></li>
                    <li className="drop-down"><Link to="/about">Who We Are</Link>
                        <ul>
                          <li><Link to="/about">Our Vision</Link></li>
                          <li><Link to="/about#mission">Our Mission</Link></li>
                          <li><Link to="/team">Our Executive Team</Link></li>
                          <li><Link to="/about#history">Our History</Link></li>
                          <li><Link to="/belief">Our Beliefs</Link></li>
                        </ul>
                            </li>
                    <li><Link to="/sermon">Sermon</Link></li>
                    <li><Link to="/events">Events</Link></li>
                    <li><Link to="/affiliations">Affiliations</Link></li>
                  </ul>
                </li>
                <li><Link to="/#services">Services</Link></li>
                <li><Link to="/Gallery">Gallery</Link></li>
                <li><Link to="/#team">Executives</Link></li>
                <li><Link to="/give">Give</Link></li>
                <li><Link to="#contact">Contact Us</Link></li>
                <li className="get-started"><Link to="/#contact">Get Started</Link></li>
            </ul>
        </nav>
      </div>
        <div className="progress-container">
       <div className="progress-bar" id="myBar"></div>
     </div>
    </header>
    </>
    )
}
}
