import React, { Component } from 'react'

class Quotes extends Component {
	constructor(props) {
		super(props);
		this.state = {
			quote: "The only person you are destined to become is the person you decide to be.",
			author: "- Ralph Waldo Emerson"
		}
		this.handleClick = this.handleClick.bind(this);
		this.randomQuote = this.randomQuote.bind(this);
	}
		handleClick() {
		fetch("https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/")
		.then(res => res.json())
		.then(data => 
				quoteData.push(data.quotes)
		)
		.catch(error => console.error("Error: ", error))

		const randomNumber = Math.floor(Math.random() * quoteData.length);
		const finalQuote =  quoteData[randomNumber];
  		this.setState({
			quote: finalQuote.quote,
			author: finalQuote.author
  })
}
	render() {
		return (
			<div id= "quote-box">
				<div id= "text">
					<h3>{this.state.quote}</h3>
				</div>
				<div id= "author">
					<p>-{this.state.author}</p>
				</div>
				<div id= "new-quote">
					<button onClick={this.handleClick} className= "btn btn-primary">New Quote</button>
				</div>
				<div >
					<a id= "tweet-quote" href= "twitter.com/intent/tweet" className= "btn btn-primary">
						Tweet Quote
					</a>
				</div>
			</div>
		)
	}
}
export default Quotes;
